mapper.select:
select idx ,severity ,`target_system` ,`target` ,`type` ,`scale_type` ,data_source ,`table` ,`metric` ,group_by ,`condition` ,message ,`status` ,date_format(regdt, '%Y-%m-%d %H:%i:%s') regdt from nexclipper_rule where 1 = 1
and target_system = #{target_system}
and target = #{target}
and metric like concat(concat('%',#{metric}),'%')
order by idx asc

select distinct target from nexclipper_rule
where target_system = #{target_system}
order by target asc

select distinct target from nexclipper_metric
where target_system = #{target_system}
order by target asc

    	select distinct alias
		from nexclipper_metric
		where target = #{target}
		
		and target_system = #{target_system}
		
		order by alias asc

    	select ifnull(a.idx, -1) idx
    		  ,b.metric
		from nexclipper_rule a
			 ,nexclipper_metric b
		where b.target = #{target}
		and  b.alias = #{metric}
		and  a.severity = #{severity}
		and  b.target_system = #{target_system}
		and  b.target_system = a.target_system
		and  b.target = a.target
		and  b.metric = a.metric
		limit 1

mapper.insert:
    	insert into nexclipper_rule
    	(
    		 target_system
    		,target
    		,data_source
    		,`table`
    		,metric
    		,math
    		,group_by
    		,`condition`
    		,message
    		,new_engine
    		,`status`
    		,notify
    		,severity
    		
    		
    		,slack_token
    		
    		
    		
    		,slack_channel
    		
    	)
    	select   #{target_system}
	    		,#{target}
	    		,data_source
	    		,`table`
	    		,metric
	    		,math
	    		,group_by
	    		,#{condition}
	    		,#{message}
	    		,new_engine
	    		,#{status}
	    		,#{notify}
	    		,#{severity}
	    		
	    		
	    		,#{slack_token}
	    		
	    		
	    		
	    		,#{slack_channel}
	    		
    	from nexclipper_metric
    	where  target = #{target}
		and  alias = #{metric}
		and  target_system = #{target_system}

mapper.update:
    	update nexclipper_rule set
			     target_system	= #{target_system}
				,target			= #{target}
				,metric			= #{metric}
				,`condition`	= #{condition}
				,message		= #{message}
				,`status`		= #{status}
				,notify			= #{notify}
				,severity		= #{severity}
				
	    		,slack_token	= #{slack_token}
	    		
	    		
	    		
	    		,slack_channel	= #{slack_channel}
	    		
		where idx = #{idx}
