mapper.select:
select idx, agent_id, severity, target_system, target, id, status, date_format(start_time,'%Y-%m-%d %H:%i:%s') as start_time, date_format(finish_time,'%Y-%m-%d %H:%i:%s') as finish_time, contents, memo, check_yn, date_format(regdt,'%Y-%m-%d %H:%i:%s') as regdt, metric, severity from nexclipper_incident where status='S' and check_yn='N'
and idx > #{idx}
order by status asc, idx asc

SELECT idx, severity, target_system , target_ip , target, metric, `condition`, id, `status`, date_format(start_time,'%Y-%m-%d %H:%i:%s') as start_time, case when status = 'P' then finish_time else date_format(finish_time,'%Y-%m-%d %H:%i:%s') end as finish_time, contents, memo, check_yn , date_format(regdt,'%Y-%m-%d %H:%i:%s') as regdt FROM nexclipper_incident where regdt >= DATE_SUB(NOW(),INTERVAL #{start_time} hour)
and id like concat(concat('%',#{id}),'%')
and target_ip = #{target_ip}
and target_system = #{target_system}
and target = #{target}
and status = #{status}
and severity = #{severity}
and contents like concat(concat('%',#{searchTxt}),'%')
order by status asc, idx desc

select start_time ,finish_time ,target ,metric ,id ,target_ip ,`status` ,target_system from ( select date_format(start_time,'%Y-%m-%d %H:%i:%s') start_time ,ifnull(date_format(finish_time,'%Y-%m-%d %H:%i:%s'), date_format(now(),'%Y-%m-%d %H:%i:%s')) finish_time ,target_system ,target ,metric ,id ,target_ip ,case when `status` = 'S' then 'Start' when `status` = 'F' then 'Closed' end `status` ,case when target_system = 'Cluster' then concat('3.',target_system) when target_system = 'Docker' then concat('2.',target_system) when target_system = 'POD' then concat('1.',target_system) when target_system = 'Host' then concat('7.',target_system) end target_system_order ,idx from nexclipper_incident where (start_time >= DATE_SUB(NOW(),INTERVAL #{start_time} hour) or ifnull(finish_time, now()) >= DATE_SUB(NOW(),INTERVAL #{start_time} hour) )
and id like concat(concat('%',#{id}),'%')
and target_ip = #{target_ip}
and target_system = #{target_system}
and target = #{target}
and severity = #{severity}
and ( id like concat(concat('%',#{searchTxt}),'%') or target_ip in ( select target_ip from nexclipper_incident where (start_time >= DATE_SUB(NOW(),INTERVAL #{start_time} hour) or ifnull(finish_time, now()) >= DATE_SUB(NOW(),INTERVAL #{start_time} hour)) and id like concat(concat('%',#{searchTxt}),'%') ) )
) a order by target_system_order asc, idx asc
