mapper.select:
select count(*) cnt
from nexclipper_incident
where target_system = #{target_system}
and   id = #{id}
and   start_time = #{start_time}
and   target = #{target}

mapper.update:
		update nexclipper_incident set
			  status 		= #{status}
			 ,finish_time	= ifnull(#{finish_time},now())
		where target_system = #{target_system}
        and   id = #{id}
        and   status		= 'S'
        and   target = #{target}

mapper.insert:
insert	into nexclipper_log
(
   	  host_ip
	, container_id
	, stream
	, log
	, time
)
values	

(
	  #{item.host_ip}
	, #{item.container_id}
	, #{item.stream}
	, #{item.log}
	, #{item.time}
)
